mapper.select:
select
a_article_id, a_author, a_publish_time, a_publish_department, user_id, a_classify, a_content
from jckc.kc_article


		and a_article_id = #{aArticleId}


		and a_author = #{aAuthor}


		and a_publish_time = #{aPublishTime}


		and a_publish_department = #{aPublishDepartment}


		and user_id = #{userId}


		and a_classify = #{aClassify}


		and a_content = #{aContent}
